import { Alert } from "@mui/material";
import { useState } from 'react';
import React from "react";
import Header from "../components/Header";
import House from "../components/House";
import {BtnAddHouse} from "../components/AddHouse";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css'
import PopupInputHouse from "./PopupInputHouse.js";


function Homescreen() {

    const [HouseContainer, setHouseContainer] = useState([]);
    const [buttonPopup, setButtonPopup] = useState(false);
    const [houses, setHouses] = useState([])


    function addComponent(){
        setHouseContainer([...HouseContainer, "House Test 1"])
      }

      function addHouse(name, adr){
          if (name.length == 0 || adr.length == 0) {
              Alert.alert("Please enter your house information before pressing submit");
              return
          }
          let id = Math.floor((1 + Math.random()) * 0x10000).toString(16).substring(1);
          setHouseContainer((currentHouseContainer) => [...currentHouseContainer, {id, name, adr}]);
      }

    function submit(details) {
        setHouses([...houses, details]);
        console.log(details);
    }

    return (
      <div>
        <Header></Header>
        <div>
           {houses.map((item) => (<House name={item.name} address={item.address} />))}
        </div>
        <button className="AddHouse" onClick={() => setButtonPopup(true)}>Add House</button>
        <PopupInputHouse setHouses={submit} trigger={buttonPopup} setTrigger={setButtonPopup}>

        </PopupInputHouse>
      </div>
    )
  } 

  export default Homescreen;
