import React from "react";
import { useState } from 'react';

function Housescreen(props){

    const [buttonPopup, setButtonPopup] = useState(false);
    const [rooms, setRooms] = useState([]);
    const [roomScreenOpened, setRoomScreenOpened] = useState(false);
    const [roomProps, setRoomProps] = useState();

    function submit(details) {
        setRooms([...rooms, details]);
        console.log(details);
    }

    function handleOpenRoom(room) {
        setRoomProps(room);
        setRoomScreenOpened(true);
    }

    return (
      <div class="test">
        {roomScreenOpened ? <Roomscreen room={roomProps} /> : <></>}
        <div>
           {rooms.map((item) => (<Room room={item} onClick={handleOpenRoom}/>))}
        </div>
        <button className="AddHouse" onClick={() => setButtonPopup(true)}>Add Room</button>
        <PopupInputRoom setRoom={submit} trigger={buttonPopup} setTrigger={setButtonPopup}>

        </PopupInputRoom>
      </div>
    )


    // return (
    //     <div class="test">
    //         <h1>{props.house.name}</h1>
    //         <button className="AddHouse">Add Room</button>
    //     </div>
    // );
}
export default Housescreen;
