import React from 'react';
import './PopupInputHouse.css';
import { useState } from 'react';

function PopupInputHouse(props) {
    const [houseName, setHouseName] = useState("Huis Naam");
    const [adress, setAdress] = useState("Adress Leuven");
    const click = () => {
        alert(adress)
    }
    const change = event => {

        setAdress(event.target.value)
    }

  return (props.trigger) ? (
    <div className="popup">
        <div className="popup-inner">
            <form>
                <label>
                    House Name:
                    <input type="text" name="House name..." />
                    Adress:
                    <input type="text" name="Adress..." />
                </label>
                <input value={houseName} />
                <input onChange = {change}
                value={adress} />
                <button onClick={click}>Submit</button>
            </form>
            <button className="close-btn" onClick={() => props.setTrigger(false)}>close</button>
            { props.children }
        </div>
    </div>
  ) : "";
}

export default PopupInputHouse;
