import { Alert } from "@mui/material";
import { useState } from 'react';
import React from "react";
import Header from "../components/Header";
import {ListHouses} from "../components/ListHouses";
import {BtnAddHouse} from "../components/AddHouse";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css'
import PopupInputHouse from "./PopupInputHouse.js";


function Homescreen(props) {

    const [HouseContainer, setHouseContainer] = useState([]);
    const [buttonPopup, setButtonPopup] = useState(false);


    const [details, setDetails] = useState({
        name: "",
        address: "",
    })

    const handleChange = (e) => {
        const {name, value} = e.target;
        setDetails((prev) => {
            return {
                ...prev,
                [name]: value
            };
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(details);
    }




    function addComponent(){
      setHouseContainer([...HouseContainer, "House Test 1"])
    }

    function addHouse(name, adr){
        if (name.length == 0 || adr.length == 0) {
            Alert.alert("Please enter your house information before pressing submit");
            return
        }
        let id = Math.floor((1 + Math.random()) * 0x10000).toString(16).substring(1);
        setHouseContainer((currentHouseContainer) => [...currentHouseContainer, {id, name, adr}]);
    }

    return (props.trigger) ? (
      <div>
        <Header></Header>

        {HouseContainer.map((item, i) => (<ListHouses text={item} />))}

        <button className="AddHouse" onClick={() => setButtonPopup(true)}>Add House</button>
        <PopupInputHouse trigger={buttonPopup} setTrigger={setButtonPopup}>
        <form onSubmit={handleSubmit}>
                <h3>Name House:</h3> <input type='text' name="name" onChange={handleChange}/>
                <h3>Address:</h3> <textarea name="address" onChange={handleChange}></textarea>
                <button type='submit'>Submit</button>
            </form>
            <button className="close-btn" onClick={() => props.setTrigger(false)}>close</button>
            { props.children }
        </PopupInputHouse>


      </div>
    ): "";
  } 

  export default Homescreen;
